  /**
   * Parses the xml for cdata.
   *
   * @param xmlStringWithCdata the xml string with cdata
   * @return the string
   */
  private static String parseXmlForCdata(String xmlStringWithCdata) {
    final String cdataStart = "<![CDATA[";
    final String cdataEnd = "]]>";
    final int start = xmlStringWithCdata.indexOf(cdataStart);
    final int end = xmlStringWithCdata.indexOf(cdataEnd);

    final String beforeCdata = xmlStringWithCdata.substring(0, start);
    final String cdata = xmlStringWithCdata.substring(start + cdataStart.length(), end);
    final String afterCdata =
        xmlStringWithCdata.substring(end + cdataEnd.length(), xmlStringWithCdata.length());

    // string before CDATA + CDATA + string after CDATA
    return beforeCdata + cdata + afterCdata;
  }
